UPDATE client_profile c SET active = FALSE FROM ( SELECT count(active) AS c , max(id) AS id , pin FROM client_profile WHERE active = TRUE GROUP BY pin) AS s WHERE s.c > 1 AND s.pin = c.pin AND c.id <> s.id and c.active=TRUE ;
drop FUNCTION make_last_client_profile_active();
create function make_last_client_profile_active() returns trigger LANGUAGE plpgsql AS $$ DECLARE ri RECORD; oldValue TEXT; newValue TEXT; isColumnSignificant BOOLEAN; isValueModified BOOLEAN; BEGIN -- based on http://crafted-software.blogspot.com.ee/2011/05/hoorah-i-was-able-to-complete-my.html
IF (TG_OP <> 'INSERT') THEN -- If operation is an INSERT, we have no OLD value, so use an empty string.
FOR ri IN -- Fetch a ResultSet listing columns defined for this trigger's table. SELECT ordinal_position, column_name, data_type FROM information_schema.columns WHERE table_schema = quote_ident(TG_TABLE_SCHEMA) AND table_name = quote_ident(TG_TABLE_NAME) ORDER BY ordinal_position LOOP -- For each column in this trigger's table, copy the OLD & NEW values into respective variables. -- NEW value EXECUTE 'SELECT ($1).' || ri.column_name || '::text' INTO newValue USING NEW;
-- OLD value EXECUTE 'SELECT ($1).' || ri.column_name || '::text' INTO oldValue USING OLD;
isColumnSignificant := (ri.column_name <> 'id') AND (ri.column_name <> 'active'); IF isColumnSignificant THEN isValueModified := oldValue <> newValue OR isValueModified; -- If this nthField in the table was modified, make history. END IF; END LOOP; END IF;
IF isValueModified AND NEW.created :: DATE <> CURRENT_DATE AND NEW.editing_user <> 'fix' THEN RAISE 'Client profiles can be changed only on the creation day by the user who created the profile . Profile id: %', NEW.id; END IF;
IF NEW.active = TRUE THEN UPDATE client_profile cp SET active = FALSE WHERE cp.pin = NEW.pin AND cp.id <> NEW.id; END IF;
RETURN NEW;
END $$;
CREATE TRIGGER update_client_profile_active BEFORE INSERT OR UPDATE ON client_profile FOR EACH ROW EXECUTE PROCEDURE make_last_client_profile_active();
